import { Component } from '@angular/core';
import { FormBuilder, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService } from '../../services/auth.service';

@Component({
  selector: 'app-auth-form',
  templateUrl: './auth-form.component.html',
  styleUrls: ['./auth-form.component.scss']
})
export class AuthFormComponent {
  mode: 'login' | 'register' = 'login';
  message = '';
  loading = false;

  form = this.fb.group({
    childName: ['', Validators.required],
    age: [10, [Validators.required, Validators.min(7), Validators.max(14)]],
    guardianName: ['', Validators.required],
    guardianEmail: ['', [Validators.required, Validators.email]],
    password: ['', [Validators.required, Validators.minLength(6)]]
  });

  constructor(private fb: FormBuilder, private auth: AuthService, private router: Router) {
    this.applyValidators();
  }

  switchMode(): void {
    this.mode = this.mode === 'login' ? 'register' : 'login';
    this.message = '';
    this.applyValidators();
  }

  submit(): void {
    if (this.form.invalid && this.mode === 'register') {
      this.message = 'Por favor revisa los datos.';
      return;
    }

    if (this.mode === 'login' && this.form.get('guardianEmail')?.invalid) {
      this.message = 'Ingresa un correo válido y tu contraseña.';
      return;
    }

    this.loading = true;
    const value = this.form.value;

    if (this.mode === 'login') {
      this.auth.login({
        email: value.guardianEmail!,
        password: value.password!
      }).subscribe({
        next: () => {
          this.loading = false;
          this.router.navigate(['/dashboard']);
        },
        error: err => {
          this.loading = false;
          this.message = err.error || 'No fue posible iniciar sesión.';
        }
      });
    } else {
      this.auth.register({
        childName: value.childName!,
        age: value.age!,
        guardianName: value.guardianName!,
        guardianEmail: value.guardianEmail!,
        password: value.password!
      }).subscribe({
        next: () => {
          this.loading = false;
          this.router.navigate(['/dashboard']);
        },
        error: err => {
          this.loading = false;
          this.message = err.error || 'No fue posible registrarse.';
        }
      });
    }
  }

  private applyValidators(): void {
    if (this.mode === 'login') {
      this.form.get('childName')?.clearValidators();
      this.form.get('age')?.clearValidators();
      this.form.get('guardianName')?.clearValidators();
    } else {
      this.form.get('childName')?.setValidators([Validators.required]);
      this.form.get('age')?.setValidators([Validators.required, Validators.min(7), Validators.max(14)]);
      this.form.get('guardianName')?.setValidators([Validators.required]);
    }
    this.form.get('childName')?.updateValueAndValidity({ emitEvent: false });
    this.form.get('age')?.updateValueAndValidity({ emitEvent: false });
    this.form.get('guardianName')?.updateValueAndValidity({ emitEvent: false });
  }
}
<div class="switcher">
  <button type="button" [class.active]="mode === 'login'" (click)="mode = 'login'">Iniciar sesión</button>
  <button type="button" [class.active]="mode === 'register'" (click)="mode = 'register'">Registrarme</button>
</div>
<form (ngSubmit)="submit()" [formGroup]="form">
  <div class="form-row" *ngIf="mode === 'register'">
    <label>Nombre del niño o niña</label>
    <input type="text" formControlName="childName" placeholder="Ej. Sofía" />
  </div>
  <div class="form-row" *ngIf="mode === 'register'">
    <label>Edad</label>
    <input type="number" min="7" max="14" formControlName="age" />
  </div>
  <div class="form-row" *ngIf="mode === 'register'">
    <label>Tutor</label>
    <input type="text" formControlName="guardianName" placeholder="Nombre del tutor" />
  </div>
  <div class="form-row">
    <label>Correo electrónico</label>
    <input type="email" formControlName="guardianEmail" placeholder="tu@correo.com" />
  </div>
  <div class="form-row">
    <label>Contraseña</label>
    <input type="password" formControlName="password" placeholder="Mínimo 6 caracteres" />
  </div>
  <p class="message" *ngIf="message">{{ message }}</p>
  <button class="btn-primary" type="submit" [disabled]="loading">
    {{ loading ? 'Procesando...' : (mode === 'login' ? 'Entrar' : 'Crear cuenta') }}
  </button>
</form>
<p class="hint">
  {{ mode === 'login' ? '¿Aún no tienes una cuenta?' : '¿Ya tienes una cuenta?' }}
  <a (click)="switchMode()" role="button">{{ mode === 'login' ? 'Crear una ahora' : 'Iniciar sesión' }}</a>
</p>
